import { createSlice } from '@reduxjs/toolkit';

export const todoSlice = createSlice({
  name:"todo",
  initialState:{
    todos:[
      {
        "cid": 2,
        "title": "睡觉",
        "isCompleted": false
      }
    ]
  },
  reducer:{
    loadTodos(){},
    addTodo(){},
    selectTodos(state,action){
          return state.todos
    }
  }
})


export default todoSlice.reducer
